import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function stringSimilarity(a, b) {
  if (!a || !b) return 0;
  a = a.toLowerCase();
  b = b.toLowerCase();
  if (a === b) return 1;
  let matches = 0;
  for (let i = 0; i < Math.min(a.length, b.length); i++) {
    if (a[i] === b[i]) matches++;
  }
  return matches / Math.max(a.length, b.length);
}

const AdminPanel = () => {
  const [reports, setReports] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [matching, setMatching] = useState({});
  const [approving, setApproving] = useState({});
  const [rejecting, setRejecting] = useState({});
  const navigate = useNavigate();

  useEffect(() => {
    const fetchReports = async () => {
      try {
        const token = localStorage.getItem('token');
        // Fetch all reports
        const response = await axios.get('http://localhost:5000/api/admin/reports', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setReports(response.data);

        // Fetch approved reports
        const approvedResponse = await axios.get('http://localhost:5000/api/admin/reports/approved', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setReports(prev => [...prev, ...approvedResponse.data]);

        // Fetch rejected reports
        const rejectedResponse = await axios.get('http://localhost:5000/api/admin/reports/rejected', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setReports(prev => [...prev, ...rejectedResponse.data]);
      } catch (err) {
        setError('Failed to fetch reports');
      } finally {
        setLoading(false);
      }
    };
    fetchReports();
  }, [success]);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    navigate('/login');
  };

  const handleMatch = async (lostId, foundId) => {
    setSuccess('');
    setError('');
    setMatching({ ...matching, [`${lostId}_${foundId}`]: true });
    try {
      const token = localStorage.getItem('token');
      await axios.post(`http://localhost:5000/api/admin/reports/${lostId}/match/${foundId}`, {}, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setSuccess('Reports matched successfully!');
    } catch (err) {
      setError('Failed to match reports');
    } finally {
      setMatching({ ...matching, [`${lostId}_${foundId}`]: false });
    }
  };

  const handleApprove = async (reportId) => {
    setSuccess('');
    setError('');
    setApproving({ ...approving, [reportId]: true });
    try {
      const token = localStorage.getItem('token');
      await axios.post(`http://localhost:5000/api/admin/reports/${reportId}/approve`, {}, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setSuccess('Report approved successfully!');
    } catch (err) {
      setError('Failed to approve report');
    } finally {
      setApproving({ ...approving, [reportId]: false });
    }
  };

  const handleReject = async (reportId, rejectionReason) => {
    setSuccess('');
    setError('');
    setRejecting({ ...rejecting, [reportId]: true });
    try {
      const token = localStorage.getItem('token');
      await axios.post(`http://localhost:5000/api/admin/reports/${reportId}/reject`, {
        rejectionReason
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setSuccess('Report rejected successfully!');
    } catch (err) {
      setError('Failed to reject report');
    } finally {
      setRejecting({ ...rejecting, [reportId]: false });
    }
  };

  const lostReports = reports.filter(r => r.type === 'lost');
  const foundReports = reports.filter(r => r.type === 'found');

  // Find possible matches for each lost report
  const getPossibleMatches = (lost) => {
    return foundReports.filter(found => {
      // Simple similarity check (can be improved)
      const nameSim = stringSimilarity(lost.item.name, found.item.name);
      const locSim = stringSimilarity(lost.location, found.location);
      return nameSim > 0.5 && locSim > 0.3 && !found.adminApproval?.matchedWith;
    });
  };

  return (
    <div className="min-h-screen w-full flex flex-col items-center justify-center relative bg-gradient-to-br from-gray-900 to-blue-900 py-12">
      <div className="max-w-6xl w-full mx-auto rounded-3xl shadow-2xl bg-white/30 backdrop-blur-2xl p-10 border border-white/40 card-3d">
        <div className="flex flex-col md:flex-row justify-between items-center mb-8 gap-4">
          <h1 className="text-4xl font-extrabold text-white font-serif tracking-tight drop-shadow-lg">Admin Panel</h1>
          <button onClick={handleLogout} className="py-2 px-5 bg-red-500 text-white rounded-xl font-bold shadow hover:bg-red-600 transition-colors duration-200">Logout</button>
        </div>
        <div className="mb-6 text-white/90 text-center font-medium">Welcome, Admin! Here you can manage all reports and approve matches.</div>
        {success && <div className="bg-green-100 border border-green-400 text-green-700 px-4 py-3 rounded mb-4">{success}</div>}
        {error && <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">{error}</div>}
        {loading ? (
          <div className="flex justify-center items-center min-h-[200px]">
            <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
          </div>
        ) : (
          <>
            {/* Tabs for different report types */}
            <div className="flex space-x-4 mb-6">
              <button
                className="px-4 py-2 rounded-lg bg-blue-600 text-white hover:bg-blue-700"
                onClick={() => navigate('/admin')}
              >
                All Reports
              </button>
              <button
                className="px-4 py-2 rounded-lg bg-blue-600 text-white hover:bg-blue-700"
                onClick={() => navigate('/admin/reports/approved')}
              >
                Approved Reports
              </button>
              <button
                className="px-4 py-2 rounded-lg bg-blue-600 text-white hover:bg-blue-700"
                onClick={() => navigate('/admin/reports/rejected')}
              >
                Rejected Reports
              </button>
            </div>

            {/* Reports Table */}
            <div className="overflow-x-auto">
              <table className="min-w-full bg-white/80 rounded-xl shadow border border-white/40">
                <thead>
                  <tr>
                    <th className="px-4 py-2">Item</th>
                    <th className="px-4 py-2">Type</th>
                    <th className="px-4 py-2">User</th>
                    <th className="px-4 py-2">Location</th>
                    <th className="px-4 py-2">Status</th>
                    <th className="px-4 py-2">Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {reports.map(report => (
                    <tr key={report._id} className="border-t border-gray-200">
                      <td className="px-4 py-2">
                        <div className="font-bold text-gray-800">{report.item.name}</div>
                        <div className="text-gray-600 text-sm">{report.item.description}</div>
                      </td>
                      <td className="px-4 py-2">{report.type}</td>
                      <td className="px-4 py-2">
                        <div className="font-semibold text-blue-800">{report.user?.name}</div>
                        <div className="text-gray-600 text-xs">{report.user?.email}</div>
                      </td>
                      <td className="px-4 py-2">{report.location}</td>
                      <td className="px-4 py-2">
                        {report.adminApproval?.status === 'matched' ? (
                          <span className="text-green-700 font-bold">Matched</span>
                        ) : report.adminApproval?.status === 'approved' ? (
                          <span className="text-blue-700 font-bold">Approved</span>
                        ) : report.adminApproval?.status === 'rejected' ? (
                          <span className="text-red-700 font-bold">Rejected</span>
                        ) : (
                          <span className="text-yellow-700 font-bold">Pending</span>
                        )}
                      </td>
                      <td className="px-4 py-2">
                        {report.adminApproval?.status === 'pending' && (
                          <>
                            <button
                              onClick={() => handleApprove(report._id)}
                              disabled={approving[report._id]}
                              className="mr-2 px-3 py-1 bg-green-600 text-white rounded hover:bg-green-700 disabled:opacity-50"
                            >
                              {approving[report._id] ? 'Approving...' : 'Approve'}
                            </button>
                            <button
                              onClick={() => {
                                const rejectionReason = prompt('Enter rejection reason:');
                                if (rejectionReason) {
                                  handleReject(report._id, rejectionReason);
                                }
                              }}
                              disabled={rejecting[report._id]}
                              className="px-3 py-1 bg-red-600 text-white rounded hover:bg-red-700 disabled:opacity-50"
                            >
                              {rejecting[report._id] ? 'Rejecting...' : 'Reject'}
                            </button>
                          </>
                        )}
                        {report.type === 'lost' && !report.adminApproval?.matchedWith && (
                          <div className="mt-2">
                            {foundReports
                              .filter(found => 
                                stringSimilarity(report.item.name, found.item.name) > 0.5 &&
                                stringSimilarity(report.location, found.location) > 0.3
                              )
                              .map(found => (
                                <div key={found._id} className="mt-2">
                                  <div className="font-bold text-green-700">{found.item.name}</div>
                                  <div className="text-gray-600 text-xs">Location: {found.location}</div>
                                  <button
                                    onClick={() => handleMatch(report._id, found._id)}
                                    disabled={matching[`${report._id}_${found._id}`]}
                                    className="mt-1 px-3 py-1 bg-blue-600 text-white rounded hover:bg-blue-700 disabled:opacity-50"
                                  >
                                    {matching[`${report._id}_${found._id}`] ? 'Matching...' : 'Match'}
                                  </button>
                                </div>
                              ))}
                          </div>
                        )}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default AdminPanel;
