import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import axios from 'axios';

const MatchedReports = () => {
  const { user } = useAuth();
  const [reports, setReports] = useState([]);
  const [matches, setMatches] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchReports();
  }, []);

  const fetchReports = async () => {
    try {
      setLoading(true);
      const response = await axios.get('http://localhost:5000/api/reports', {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });

      if (response.data.success) {
        setReports(response.data.reports);

        // Fetch matches for each report
        const matchesData = {};
        for (const report of response.data.reports) {
          const matchesResponse = await axios.get(`http://localhost:5000/api/reports/matches/${report._id}`, {
            headers: {
              'Authorization': `Bearer ${localStorage.getItem('token')}`
            }
          });

          if (matchesResponse.data.success) {
            matchesData[report._id] = matchesResponse.data.matches;
          }
        }
        setMatches(matchesData);
      }
    } catch (err) {
      console.error('Error fetching reports:', err);
      setError('Failed to fetch reports. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'active':
        return 'bg-green-100 text-green-800';
      case 'resolved':
        return 'bg-blue-100 text-blue-800';
      case 'closed':
        return 'bg-gray-100 text-gray-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  const getTypeColor = (type) => {
    return type === 'lost' ? 'bg-red-100 text-red-800' : 'bg-green-100 text-green-800';
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 py-8">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center">
            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto"></div>
            <p className="mt-4 text-gray-600">Loading matched reports...</p>
          </div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen bg-gray-50 py-8">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="bg-red-50 border border-red-200 rounded-md p-4">
            <p className="text-red-800">{error}</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900">Matched Reports</h1>
          <p className="mt-2 text-gray-600">
            View potential matches between your lost and found reports
          </p>
        </div>

        {reports.length === 0 ? (
          <div className="text-center py-12">
            <div className="text-gray-400 mb-4">
              <svg className="mx-auto h-12 w-12" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
              </svg>
            </div>
            <h3 className="text-lg font-medium text-gray-900 mb-2">No reports found</h3>
            <p className="text-gray-500">Create some reports to see potential matches.</p>
          </div>
        ) : (
          <div className="space-y-6">
            {reports.map((report) => (
              <div key={report._id} className="bg-white shadow rounded-lg p-6">
                <div className="flex items-center justify-between mb-4">
                  <div className="flex items-center space-x-3">
                    <span className={`px-2 py-1 text-xs font-medium rounded-full ${getTypeColor(report.type)}`}>
                      {report.type.toUpperCase()}
                    </span>
                    <span className={`px-2 py-1 text-xs font-medium rounded-full ${getStatusColor(report.status)}`}>
                      {report.status}
                    </span>
                  </div>
                  <span className="text-sm text-gray-500">
                    {new Date(report.createdAt).toLocaleDateString()}
                  </span>
                </div>

                <div className="mb-4">
                  <h3 className="text-lg font-semibold text-gray-900 mb-2">
                    {report.item}
                  </h3>
                  <p className="text-gray-600 mb-2">
                    <strong>Location:</strong> {report.location}
                  </p>
                  {report.contactInfo && (
                    <p className="text-gray-600 mb-2">
                      <strong>Contact:</strong> {report.contactInfo}
                    </p>
                  )}
                  <p className="text-gray-600">
                    <strong>Description:</strong> {report.description || 'No description provided'}
                  </p>
                </div>

                {/* Matches Section */}
                {matches[report._id] && matches[report._id].length > 0 ? (
                  <div className="mt-6">
                    <h4 className="text-md font-semibold text-gray-900 mb-3">
                      Potential Matches ({matches[report._id].length})
                    </h4>
                    <div className="space-y-3">
                      {matches[report._id].map((match) => (
                        <div key={match._id} className="border border-gray-200 rounded-lg p-4 bg-gray-50">
                          <div className="flex items-center justify-between mb-2">
                            <span className={`px-2 py-1 text-xs font-medium rounded-full ${getTypeColor(match.type)}`}>
                              {match.type.toUpperCase()}
                            </span>
                            <span className="text-sm text-gray-500">
                              {new Date(match.createdAt).toLocaleDateString()}
                            </span>
                          </div>
                          <h5 className="font-medium text-gray-900 mb-1">
                            {match.item}
                          </h5>
                          <p className="text-sm text-gray-600 mb-2">
                            <strong>Location:</strong> {match.location}
                          </p>
                          {match.contactInfo && (
                            <p className="text-sm text-gray-600 mb-2">
                              <strong>Contact:</strong> {match.contactInfo}
                            </p>
                          )}
                          <p className="text-sm text-gray-600">
                            <strong>Posted by:</strong> {match.user.name}
                          </p>
                        </div>
                      ))}
                    </div>
                  </div>
                ) : (
                  <div className="mt-6 text-center py-4">
                    <p className="text-gray-500">No potential matches found for this report.</p>
                  </div>
                )}
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default MatchedReports;
