import React, { createContext, useContext, useEffect, useState } from 'react';
import axios from 'axios';
import { API_BASE_URL, endpoints, getAuthHeader } from '../config/api';

const AuthContext = createContext(null);

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Load user data from localStorage and validate token
  useEffect(() => {
    const loadUser = async () => {
      const storedUser = localStorage.getItem('user');
      const storedToken = localStorage.getItem('token');

      if (storedUser && storedToken) {
        try {
          // Validate token and get fresh user data
          const response = await axios.get(
            `${API_BASE_URL}${endpoints.profile.get}`,
            getAuthHeader()
          );

          if (response.data.success) {
            const userData = response.data.data;
            setUser(userData);
            localStorage.setItem('user', JSON.stringify(userData));
          } else {
            // Token is invalid, clear storage
            localStorage.removeItem('user');
            localStorage.removeItem('token');
          }
        } catch (err) {
          console.error('Error loading user:', err);
          localStorage.removeItem('user');
          localStorage.removeItem('token');
        }
      }

      setLoading(false);
    };

    loadUser();

    // Listen for changes in localStorage
    const handleStorageChange = (e) => {
      if (e.key === 'user' || e.key === 'token') {
        loadUser();
      }
    };

    window.addEventListener('storage', handleStorageChange);
    return () => window.removeEventListener('storage', handleStorageChange);
  }, []);

  const updateUserProfile = async (userData) => {
    try {
      const response = await axios.put(
        `${API_BASE_URL}${endpoints.profile.update}`,
        userData,
        getAuthHeader()
      );

      if (response.data.success) {
        const updatedUser = { ...user, ...response.data.data };
        setUser(updatedUser);
        localStorage.setItem('user', JSON.stringify(updatedUser));
        return { success: true, data: updatedUser };
      }
      return { success: false, error: response.data.message };
    } catch (err) {
      console.error('Error updating profile:', err);
      const errorMsg = err.response?.data?.message || 'Failed to update profile';
      setError(errorMsg);
      return { success: false, error: errorMsg };
    }
  };

  const uploadProfilePhoto = async (file) => {
    const formData = new FormData();
    formData.append('profilePicture', file);

    try {
      const response = await axios.post(
        `${API_BASE_URL}${endpoints.profile.uploadPhoto}`,
        formData,
        {
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`,
            'Content-Type': 'multipart/form-data'
          }
        }
      );

      if (response.data.success) {
        const updatedUser = { 
          ...user, 
          profilePicture: response.data.data.profilePicture 
        };
        setUser(updatedUser);
        localStorage.setItem('user', JSON.stringify(updatedUser));
        return { success: true, data: updatedUser };
      }
      return { success: false, error: response.data.message };
    } catch (err) {
      console.error('Error uploading photo:', err);
      const errorMsg = err.response?.data?.message || 'Failed to upload photo';
      setError(errorMsg);
      return { success: false, error: errorMsg };
    }
  };

  const removeProfilePhoto = async () => {
    try {
      const response = await axios.delete(
        `${API_BASE_URL}${endpoints.profile.removePhoto}`,
        getAuthHeader()
      );

      if (response.data.success) {
        const updatedUser = { ...user, profilePicture: null };
        setUser(updatedUser);
        localStorage.setItem('user', JSON.stringify(updatedUser));
        return { success: true };
      }
      return { success: false, error: response.data.message };
    } catch (err) {
      console.error('Error removing photo:', err);
      const errorMsg = err.response?.data?.message || 'Failed to remove photo';
      setError(errorMsg);
      return { success: false, error: errorMsg };
    }
  };

  const login = (userData, token) => {
    localStorage.setItem('user', JSON.stringify(userData));
    localStorage.setItem('token', token);
    setUser(userData);
    setError(null);
  };

  const logout = () => {
    localStorage.removeItem('user');
    localStorage.removeItem('token');
    setUser(null);
    setError(null);
  };

  return (
    <AuthContext.Provider value={{ 
      user, 
      loading, 
      error,
      login, 
      logout,
      updateUserProfile,
      uploadProfilePhoto,
      removeProfilePhoto
    }}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (context === undefined) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};
