import React, { useState, useEffect } from 'react';
import axios from 'axios';

const MyReports = () => {
  const [reports, setReports] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchReports = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get(
          'http://localhost:5000/api/reports',
          {
            headers: {
              Authorization: `Bearer ${token}`
            }
          }
        );
        setReports(response.data.reports);
      } catch (err) {
        setError(err.response?.data?.message || 'Failed to fetch reports');
      } finally {
        setLoading(false);
      }
    };

    fetchReports();
  }, []);

  const lostReports = reports.filter(report => report.type === 'lost');
  const foundReports = reports.filter(report => report.type === 'found');

  return (
    <div
      className="min-h-screen w-full flex flex-col items-center justify-center relative bg-cover bg-center"
      style={{
        backgroundImage: `url(${process.env.PUBLIC_URL + '/kkwcolle.jpg'})`,
      }}
    >
      {/* Overlay */}
      <div className="absolute inset-0 bg-black bg-opacity-60 z-0" />
      <div className="relative z-10 w-full flex flex-col items-center justify-center py-16">
        <div className="max-w-3xl w-full mx-auto rounded-3xl shadow-2xl bg-white/30 backdrop-blur-2xl p-10 border border-white/40 card-3d">
          <div className="flex flex-col items-center mb-8">
            <h1 className="text-4xl font-extrabold text-white mb-2 font-serif tracking-tight drop-shadow-lg flex items-center gap-3">
              <svg className="w-10 h-10 text-blue-200" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 16h-1v-4h-1m1-4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
              </svg>
              My Reports
            </h1>
            <p className="text-white/90 text-center mb-4">View and manage all your lost and found reports at KKW College.</p>
          </div>
          {loading ? (
            <div className="flex justify-center items-center min-h-[200px]">
              <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
            </div>
          ) : error ? (
            <div className="bg-red-50 border border-red-400 text-red-700 px-4 py-3 rounded">
              {error}
            </div>
          ) : (
            <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
              <div>
                <h2 className="text-xl font-bold text-red-700 mb-3 flex items-center gap-2">
                  <svg className="w-7 h-7 text-red-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M18.364 18.364A9 9 0 005.636 5.636m12.728 12.728A9 9 0 015.636 5.636m12.728 12.728l1.5-1.5m-1.5-1.5l-1.5-1.5" />
                  </svg>
                  Lost Items
                </h2>
                {lostReports.length === 0 ? (
                  <p className="text-gray-200">No lost items reported yet</p>
                ) : (
                  <div className="space-y-4">
                    {lostReports.map((report) => (
                      <div key={report._id} className="bg-white/80 p-4 rounded-xl shadow card-3d border border-white/40">
                        <h3 className="text-lg font-semibold mb-1 text-gray-800">{report.item.name}</h3>
                        <p className="text-gray-600 mb-1">{report.item.description}</p>
                        <p className="text-gray-600 mb-1">Last seen at: {report.location}</p>
                        <p className="text-sm text-gray-500 mb-1">Reported on: {new Date(report.createdAt).toLocaleDateString()}</p>
                      </div>
                    ))}
                  </div>
                )}
              </div>
              <div>
                <h2 className="text-xl font-bold text-green-700 mb-3 flex items-center gap-2">
                  <svg className="w-7 h-7 text-green-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
                  </svg>
                  Found Items
                </h2>
                {foundReports.length === 0 ? (
                  <p className="text-gray-200">No found items reported yet</p>
                ) : (
                  <div className="space-y-4">
                    {foundReports.map((report) => (
                      <div key={report._id} className="bg-white/80 p-4 rounded-xl shadow card-3d border border-white/40">
                        <h3 className="text-lg font-semibold mb-1 text-gray-800">{report.item.name}</h3>
                        <p className="text-gray-600 mb-1">{report.item.description}</p>
                        <p className="text-gray-600 mb-1">Found at: {report.location}</p>
                        <p className="text-gray-600 mb-1">Contact: {report.contactInfo}</p>
                        <p className="text-sm text-gray-500 mb-1">Reported on: {new Date(report.createdAt).toLocaleDateString()}</p>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default MyReports;
