import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function stringSimilarity(a, b) {
  if (!a || !b) return 0;
  a = a.toLowerCase();
  b = b.toLowerCase();
  if (a === b) return 1;
  let matches = 0;
  for (let i = 0; i < Math.min(a.length, b.length); i++) {
    if (a[i] === b[i]) matches++;
  }
  return matches / Math.max(a.length, b.length);
}

const AdminPanel = () => {
  const [reports, setReports] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [matching, setMatching] = useState({});
  const navigate = useNavigate();

  useEffect(() => {
    const fetchReports = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get('http://localhost:5000/api/admin/reports', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setReports(response.data);
      } catch (err) {
        setError('Failed to fetch reports');
      } finally {
        setLoading(false);
      }
    };
    fetchReports();
  }, [success]);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    navigate('/login');
  };

  const handleMatch = async (lostId, foundId) => {
    setSuccess('');
    setError('');
    setMatching({ ...matching, [`${lostId}_${foundId}`]: true });
    try {
      const token = localStorage.getItem('token');
      await axios.post(`http://localhost:5000/api/admin/reports/${lostId}/match/${foundId}`, {}, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setSuccess('Reports matched successfully!');
    } catch (err) {
      setError('Failed to match reports');
    } finally {
      setMatching({ ...matching, [`${lostId}_${foundId}`]: false });
    }
  };

  const lostReports = reports.filter(r => r.type === 'lost');
  const foundReports = reports.filter(r => r.type === 'found');

  // Find possible matches for each lost report
  const getPossibleMatches = (lost) => {
    return foundReports.filter(found => {
      // Simple similarity check (can be improved)
      const nameSim = stringSimilarity(lost.item.name, found.item.name);
      const locSim = stringSimilarity(lost.location, found.location);
      return nameSim > 0.5 && locSim > 0.3 && !found.adminApproval?.matchedWith;
    });
  };

  return (
    <div className="min-h-screen w-full flex flex-col items-center justify-center relative bg-gradient-to-br from-gray-900 to-blue-900 py-12">
      <div className="max-w-6xl w-full mx-auto rounded-3xl shadow-2xl bg-white/30 backdrop-blur-2xl p-10 border border-white/40 card-3d">
        <div className="flex flex-col md:flex-row justify-between items-center mb-8 gap-4">
          <h1 className="text-4xl font-extrabold text-white font-serif tracking-tight drop-shadow-lg">Admin Panel</h1>
          <button onClick={handleLogout} className="py-2 px-5 bg-red-500 text-white rounded-xl font-bold shadow hover:bg-red-600 transition-colors duration-200">Logout</button>
        </div>
        <div className="mb-6 text-white/90 text-center font-medium">Welcome, Admin! Here you can view all user lost and found reports, and approve matches as a mediator.</div>
        {success && <div className="bg-green-100 border border-green-400 text-green-700 px-4 py-3 rounded mb-4">{success}</div>}
        {error && <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">{error}</div>}
        {loading ? (
          <div className="flex justify-center items-center min-h-[200px]">
            <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
          </div>
        ) : (
          <div className="overflow-x-auto">
            <table className="min-w-full bg-white/80 rounded-xl shadow border border-white/40">
              <thead>
                <tr>
                  <th className="px-4 py-2">Lost Item</th>
                  <th className="px-4 py-2">Lost By</th>
                  <th className="px-4 py-2">Possible Found Matches</th>
                  <th className="px-4 py-2">Status</th>
                </tr>
              </thead>
              <tbody>
                {lostReports.map(lost => (
                  <tr key={lost._id} className="border-t border-gray-200">
                    <td className="px-4 py-2 align-top">
                      <div className="font-bold text-gray-800">{lost.item.name}</div>
                      <div className="text-gray-600 text-sm">{lost.item.description}</div>
                      <div className="text-gray-500 text-xs">Location: {lost.location}</div>
                    </td>
                    <td className="px-4 py-2 align-top">
                      <div className="font-semibold text-blue-800">{lost.user?.name}</div>
                      <div className="text-gray-600 text-xs">{lost.user?.email}</div>
                    </td>
                    <td className="px-4 py-2 align-top">
                      {getPossibleMatches(lost).length === 0 ? (
                        <div className="text-gray-400">No likely found matches</div>
                      ) : (
                        <div className="space-y-2">
                          {getPossibleMatches(lost).map(found => (
                            <div key={found._id} className="bg-green-50 p-2 rounded shadow flex flex-col gap-1">
                              <div className="font-bold text-green-700">{found.item.name}</div>
                              <div className="text-gray-600 text-xs">{found.item.description}</div>
                              <div className="text-gray-500 text-xs">Location: {found.location}</div>
                              <div className="text-gray-500 text-xs">Found By: {found.user?.name} ({found.user?.email})</div>
                              <button
                                className="mt-1 py-1 px-3 bg-blue-600 text-white rounded hover:bg-blue-700 text-xs font-bold disabled:opacity-50"
                                disabled={matching[`${lost._id}_${found._id}`] || lost.adminApproval?.status === 'matched'}
                                onClick={() => handleMatch(lost._id, found._id)}
                              >
                                {lost.adminApproval?.status === 'matched' ? 'Matched' : matching[`${lost._id}_${found._id}`] ? 'Matching...' : 'Approve Match'}
                              </button>
                            </div>
                          ))}
                        </div>
                      )}
                    </td>
                    <td className="px-4 py-2 align-top">
                      {lost.adminApproval?.status === 'matched' ? (
                        <span className="text-green-700 font-bold">Matched</span>
                      ) : (
                        <span className="text-yellow-700 font-bold">Pending</span>
                      )}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
};

export default AdminPanel;
