import React, { useEffect } from 'react';
import { Navigate, useNavigate } from 'react-router-dom';
import axios from 'axios';

const ProtectedRoute = ({ children }) => {
  const token = localStorage.getItem('token');
  const navigate = useNavigate();

  useEffect(() => {
    const checkAuth = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/auth/me', {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });

        // Check for notifications
        const notificationsResponse = await axios.get('http://localhost:5000/api/reports/notifications', {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });

        if (notificationsResponse.data.success && notificationsResponse.data.notifications.length > 0) {
          // Store notifications in localStorage
          localStorage.setItem('notifications', JSON.stringify(notificationsResponse.data.notifications));
        }
      } catch (error) {
        console.error('Auth check error:', error);
        localStorage.removeItem('token');
        navigate('/login');
      }
    };

    if (token) {
      checkAuth();
    }
  }, [token, navigate]);

  if (!token) {
    return <Navigate to="/login" replace />;
  }

  return children;
};

export default ProtectedRoute;
