import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

function stringSimilarity(a, b) {
  if (!a || !b) return 0;
  a = a.toLowerCase();
  b = b.toLowerCase();
  if (a === b) return 1;
  let matches = 0;
  for (let i = 0; i < Math.min(a.length, b.length); i++) {
    if (a[i] === b[i]) matches++;
  }
  return matches / Math.max(a.length, b.length);
}

const AdminPanel = () => {
  const { user } = useAuth();
  const [reports, setReports] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [matching, setMatching] = useState({});
  const [approving, setApproving] = useState({});
  const [rejecting, setRejecting] = useState({});
  const navigate = useNavigate();

  useEffect(() => {
    const fetchReports = async () => {
      try {
        if (!user?.token) {
          setError('Not authenticated');
          navigate('/login');
          return;
        }

        const response = await axios.get(`${process.env.REACT_APP_API_URL}/api/admin/reports`, {
          headers: { Authorization: `Bearer ${user.token}` }
        });

        if (!response.data.success) {
          throw new Error(response.data.message || 'Failed to fetch reports');
        }

        setReports(response.data.reports);
      } catch (err) {
        setError(err.response?.data?.message || 'Failed to fetch reports');
      } finally {
        setLoading(false);
      }
    };
    fetchReports();
  }, [success, user, navigate]);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    localStorage.removeItem('isAdmin');
    navigate('/login');
  };

  const handleMatch = async (lostId, foundId) => {
    setSuccess('');
    setError('');
    setMatching({ ...matching, [`${lostId}_${foundId}`]: true });
    try {
      const token = localStorage.getItem('token');
      await axios.post(`${process.env.REACT_APP_API_URL}/api/admin/reports/${lostId}/match/${foundId}`, {}, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setSuccess('Reports matched successfully!');
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to match reports');
    } finally {
      setMatching({ ...matching, [`${lostId}_${foundId}`]: false });
    }
  };

  const handleApprove = async (reportId) => {
    setSuccess('');
    setError('');
    setApproving({ ...approving, [reportId]: true });
    try {
      const token = localStorage.getItem('token');
      await axios.post(`${process.env.REACT_APP_API_URL}/api/admin/reports/${reportId}/approve`, {}, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setSuccess('Report approved successfully!');
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to approve report');
    } finally {
      setApproving({ ...approving, [reportId]: false });
    }
  };

  const handleReject = async (reportId, rejectionReason) => {
    setSuccess('');
    setError('');
    setRejecting({ ...rejecting, [reportId]: true });
    try {
      const token = localStorage.getItem('token');
      await axios.post(`${process.env.REACT_APP_API_URL}/api/admin/reports/${reportId}/reject`, {
        rejectionReason
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setSuccess('Report rejected successfully!');
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to reject report');
    } finally {
      setRejecting({ ...rejecting, [reportId]: false });
    }
  };

  // Filter reports by type and status
  const [currentTab, setCurrentTab] = useState('all');
  const filteredReports = reports.filter(report => {
    if (currentTab === 'all') return true;
    if (currentTab === 'approved') return report.adminApproval?.status === 'approved';
    if (currentTab === 'rejected') return report.adminApproval?.status === 'rejected';
    return false;
  });

  // Find possible matches for a lost report
  const getMatches = (lostReport) => {
    return reports.filter(found => {
      if (found.type !== 'found') return false;
      // Simple similarity check (can be improved)
      const nameSim = stringSimilarity(lostReport.item.name, found.item.name);
      const locSim = stringSimilarity(lostReport.location, found.location);
      return nameSim > 0.5 && locSim > 0.3 && !found.adminApproval?.matchedWith;
    });
  };

  return (
    <div className="min-h-screen w-full flex flex-col items-center justify-center relative bg-gradient-to-br from-gray-900 to-blue-900 py-12">
      <div className="max-w-6xl w-full mx-auto rounded-3xl shadow-2xl bg-white/30 backdrop-blur-2xl p-10 border border-white/40 card-3d">
        <div className="flex flex-col md:flex-row justify-between items-center mb-8 gap-4">
          <h1 className="text-4xl font-extrabold text-white font-serif tracking-tight drop-shadow-lg">Admin Panel</h1>
          <button onClick={handleLogout} className="py-2 px-5 bg-red-500 text-white rounded-xl font-bold shadow hover:bg-red-600 transition-colors duration-200">Logout</button>
        </div>
        <div className="mb-6 text-white/90 text-center font-medium">Welcome, Admin! Here you can manage all reports and approve matches.</div>
        {success && <div className="bg-green-100 border border-green-400 text-green-700 px-4 py-3 rounded mb-4">{success}</div>}
        {error && <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">{error}</div>}
        {loading ? (
          <div className="flex justify-center items-center min-h-[200px]">
            <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
          </div>
        ) : (
          <>
            {/* Tabs for different report types */}
            <div className="flex space-x-4 mb-6">
              <button
                className={`px-4 py-2 rounded-lg ${currentTab === 'all' ? 'bg-blue-600' : 'bg-blue-500'} text-white hover:bg-blue-700`}
                onClick={() => setCurrentTab('all')}
              >
                All Reports
              </button>
              <button
                className={`px-4 py-2 rounded-lg ${currentTab === 'approved' ? 'bg-blue-600' : 'bg-blue-500'} text-white hover:bg-blue-700`}
                onClick={() => setCurrentTab('approved')}
              >
                Approved Reports
              </button>
              <button
                className={`px-4 py-2 rounded-lg ${currentTab === 'rejected' ? 'bg-blue-600' : 'bg-blue-500'} text-white hover:bg-blue-700`}
                onClick={() => setCurrentTab('rejected')}
              >
                Rejected Reports
              </button>
            </div>

            {/* Reports Table */}
            <div className="overflow-x-auto">
              <table className="min-w-full bg-white/80 rounded-xl shadow border border-white/40">
                <thead>
                  <tr>
                    <th className="px-4 py-2">Item</th>
                    <th className="px-4 py-2">Type</th>
                    <th className="px-4 py-2">User</th>
                    <th className="px-4 py-2">Location</th>
                    <th className="px-4 py-2">Status</th>
                    <th className="px-4 py-2">Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredReports.map(report => (
                    <tr key={report._id} className="border-t border-gray-200">
                      <td className="px-4 py-2">
                        <div className="font-bold text-gray-800">{report.item.name}</div>
                        <div className="text-gray-600 text-sm">{report.item.description}</div>
                      </td>
                      <td className="px-4 py-2">{report.type}</td>
                      <td className="px-4 py-2">
                        <div className="font-semibold text-blue-800">{report.user?.name}</div>
                        <div className="text-gray-600 text-xs">{report.user?.email}</div>
                      </td>
                      <td className="px-4 py-2">{report.location}</td>
                      <td className="px-4 py-2">
                        {report.adminApproval?.status === 'matched' ? (
                          <span className="text-green-700 font-bold">Matched</span>
                        ) : report.adminApproval?.status === 'approved' ? (
                          <span className="text-blue-700 font-bold">Approved</span>
                        ) : report.adminApproval?.status === 'rejected' ? (
                          <span className="text-red-700 font-bold">Rejected</span>
                        ) : (
                          <span className="text-gray-600 font-bold">Pending</span>
                        )}
                      </td>
                      <td className="px-4 py-2">
                        {report.type === 'lost' && report.adminApproval?.status !== 'matched' && (
                          <div className="flex flex-col space-y-2">
                            {getMatches(report).map(found => (
                              <button
                                key={found._id}
                                onClick={() => handleMatch(report._id, found._id)}
                                disabled={matching[`${report._id}_${found._id}`]}
                                className={`px-4 py-2 rounded-lg ${matching[`${report._id}_${found._id}`] ? 'bg-gray-400' : 'bg-green-600'} text-white hover:bg-green-700`}
                              >
                                {matching[`${report._id}_${found._id}`] ? 'Matching...' : 'Match with ' + found.item.name}
                              </button>
                            ))}
                          </div>
                        )}
                        {report.adminApproval?.status === 'pending' && (
                          <div className="flex space-x-2">
                            <button
                              onClick={() => handleApprove(report._id)}
                              disabled={approving[report._id]}
                              className={`px-4 py-2 rounded-lg ${approving[report._id] ? 'bg-gray-400' : 'bg-blue-600'} text-white hover:bg-blue-700`}
                            >
                              {approving[report._id] ? 'Approving...' : 'Approve'}
                            </button>
                            <button
                              onClick={() => {
                                const rejectionReason = prompt('Enter rejection reason:');
                                if (rejectionReason) {
                                  handleReject(report._id, rejectionReason);
                                }
                              }}
                              disabled={rejecting[report._id]}
                              className={`px-4 py-2 rounded-lg ${rejecting[report._id] ? 'bg-gray-400' : 'bg-red-600'} text-white hover:bg-red-700`}
                            >
                              {rejecting[report._id] ? 'Rejecting...' : 'Reject'}
                            </button>
                          </div>
                        )}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default AdminPanel;
