import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { MagnifyingGlassIcon, UserIcon, EnvelopeIcon, CheckCircleIcon, ExclamationTriangleIcon } from '@heroicons/react/24/outline';

const MatchedReports = () => {
  const [matches, setMatches] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchApprovedMatches();
  }, []);

  const fetchApprovedMatches = async () => {
    try {
      setLoading(true);
      setError('');

      const response = await axios.get('http://localhost:5000/api/reports/approved-matches', {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });

      if (response.data.success) {
        setMatches(response.data.matches || []);
      } else {
        setError('Failed to fetch approved matches');
      }
    } catch (err) {
      console.error('Error fetching approved matches:', err);
      setError('Failed to fetch approved matches. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const getTypeColor = (type) => type === 'lost' ? 'bg-red-100 text-red-800 border-red-200' : 'bg-green-100 text-green-800 border-green-200';
  const getTypeIcon = (type) => type === 'lost' ? ExclamationTriangleIcon : CheckCircleIcon;

  const getItemName = (item) => {
    if (typeof item === 'string') return item;
    if (item && typeof item === 'object' && item.name) return item.name;
    return 'Unknown Item';
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 py-8">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center">
            <div className="animate-spin rounded-full h-16 w-16 border-b-4 border-blue-600 mx-auto"></div>
            <p className="mt-6 text-xl text-gray-700 font-medium">Loading approved matches...</p>
          </div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 py-8">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center">
            <div className="bg-red-50 border border-red-200 text-red-700 px-4 py-3 rounded-lg max-w-md mx-auto">
              {error}
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 py-8">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Header */}
        <div className="text-center mb-12">
          <div className="inline-flex items-center justify-center w-16 h-16 bg-gradient-to-r from-green-600 to-blue-600 rounded-full mb-6">
            <CheckCircleIcon className="h-8 w-8 text-white" />
          </div>
          <h1 className="text-4xl font-bold text-gray-900 mb-4">Approved Matches</h1>
          <p className="text-xl text-gray-600 max-w-2xl mx-auto">
            These are your items that have been matched and approved by admin. Contact the other user to arrange pickup.
          </p>
        </div>

        {matches.length === 0 ? (
          <div className="text-center py-12">
            <div className="bg-white rounded-2xl shadow-xl p-8 max-w-md mx-auto">
              <div className="w-16 h-16 bg-gray-100 rounded-full flex items-center justify-center mx-auto mb-4">
                <MagnifyingGlassIcon className="h-8 w-8 text-gray-400" />
              </div>
              <h3 className="text-lg font-semibold text-gray-900 mb-2">No Approved Matches</h3>
              <p className="text-gray-600 mb-6">
                You don't have any approved matches yet. When admin approves a match for your items, they will appear here.
              </p>
              <button
                onClick={fetchApprovedMatches}
                className="bg-blue-600 text-white px-6 py-2 rounded-lg hover:bg-blue-700 transition-colors"
              >
                Refresh
              </button>
            </div>
          </div>
        ) : (
          <div className="space-y-8">
            {matches.map((match, index) => {
              const LostIcon = getTypeIcon('lost');
              const FoundIcon = getTypeIcon('found');

              return (
                <div key={index} className="bg-white rounded-2xl shadow-xl overflow-hidden">
                  {/* Match Header */}
                  <div className="bg-gradient-to-r from-green-600 to-blue-600 px-6 py-4">
                    <div className="flex items-center justify-between">
                      <div className="flex items-center space-x-3">
                        <CheckCircleIcon className="h-6 w-6 text-white" />
                        <h3 className="text-lg font-semibold text-white">Admin Approved Match</h3>
                      </div>
                      <div className="text-white text-sm">
                        Approved on {new Date(match.approvedAt || Date.now()).toLocaleDateString()}
                      </div>
                    </div>
                  </div>

                  <div className="p-6">
                    <div className="grid md:grid-cols-2 gap-8">
                      {/* Lost Item Card */}
                      <div className="bg-red-50 rounded-xl p-6 border border-red-200">
                        <div className="flex items-center space-x-3 mb-4">
                          <LostIcon className="h-6 w-6 text-red-600" />
                          <h4 className="text-lg font-semibold text-gray-900">Lost Item</h4>
                        </div>
                        <div className="space-y-3">
                          <div>
                            <p className="text-sm font-medium text-gray-600">Item Name</p>
                            <p className="text-gray-900 font-semibold">{getItemName(match.lostReport.item)}</p>
                          </div>
                          <div>
                            <p className="text-sm font-medium text-gray-600">Location</p>
                            <p className="text-gray-900">{match.lostReport.location}</p>
                          </div>
                          <div>
                            <p className="text-sm font-medium text-gray-600">Reported By</p>
                            <div className="flex items-center space-x-2">
                              <UserIcon className="h-4 w-4 text-gray-400" />
                              <span className="text-gray-900">{match.lostReport.user.name}</span>
                            </div>
                            <div className="flex items-center space-x-2 mt-1">
                              <EnvelopeIcon className="h-4 w-4 text-gray-400" />
                              <span className="text-gray-900">{match.lostReport.user.email}</span>
                            </div>
                          </div>
                        </div>
                      </div>

                      {/* Found Item Card */}
                      <div className="bg-green-50 rounded-xl p-6 border border-green-200">
                        <div className="flex items-center space-x-3 mb-4">
                          <FoundIcon className="h-6 w-6 text-green-600" />
                          <h4 className="text-lg font-semibold text-gray-900">Found Item</h4>
                        </div>
                        <div className="space-y-3">
                          <div>
                            <p className="text-sm font-medium text-gray-600">Item Name</p>
                            <p className="text-gray-900 font-semibold">{getItemName(match.foundReport.item)}</p>
                          </div>
                          <div>
                            <p className="text-sm font-medium text-gray-600">Location</p>
                            <p className="text-gray-900">{match.foundReport.location}</p>
                          </div>
                          <div>
                            <p className="text-sm font-medium text-gray-600">Found By</p>
                            <div className="flex items-center space-x-2">
                              <UserIcon className="h-4 w-4 text-gray-400" />
                              <span className="text-gray-900">{match.foundReport.user.name}</span>
                            </div>
                            <div className="flex items-center space-x-2 mt-1">
                              <EnvelopeIcon className="h-4 w-4 text-gray-400" />
                              <span className="text-gray-900">{match.foundReport.user.email}</span>
                            </div>
                            {match.foundReport.contactInfo && (
                              <div className="mt-2">
                                <p className="text-sm font-medium text-gray-600">Contact Info</p>
                                <p className="text-gray-900">{match.foundReport.contactInfo}</p>
                              </div>
                            )}
                          </div>
                        </div>
                      </div>
                    </div>

                    {/* Action Buttons */}
                    <div className="mt-6 flex flex-wrap gap-3">
                      <button
                        onClick={() => window.open(`mailto:${match.lostReport.user.email}`, '_blank')}
                        className="bg-blue-600 text-white px-6 py-3 rounded-lg hover:bg-blue-700 transition-colors flex items-center space-x-2"
                      >
                        <EnvelopeIcon className="h-4 w-4" />
                        <span>Contact Lost Owner</span>
                      </button>
                      <button
                        onClick={() => window.open(`mailto:${match.foundReport.user.email}`, '_blank')}
                        className="bg-green-600 text-white px-6 py-3 rounded-lg hover:bg-green-700 transition-colors flex items-center space-x-2"
                      >
                        <EnvelopeIcon className="h-4 w-4" />
                        <span>Contact Found Owner</span>
                      </button>
                    </div>
                  </div>
                </div>
              );
            })}
          </div>
        )}

        {/* Refresh Button */}
        <div className="text-center mt-8">
          <button
            onClick={fetchApprovedMatches}
            className="bg-blue-600 text-white px-8 py-3 rounded-lg hover:bg-blue-700 transition-colors font-medium"
          >
            Refresh Matches
          </button>
        </div>
      </div>
    </div>
  );
};

export default MatchedReports;
