import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';

const Navbar = () => {
  const navigate = useNavigate();
  const [menuOpen, setMenuOpen] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false);

  useEffect(() => {
    const checkAdminStatus = () => {
      const user = JSON.parse(localStorage.getItem('user')) || {};
      setIsAdmin(user.isAdmin || false);
    };

    // Check admin status initially
    checkAdminStatus();

    // Listen for changes in localStorage
    window.addEventListener('storage', checkAdminStatus);

    // Cleanup event listener
    return () => {
      window.removeEventListener('storage', checkAdminStatus);
    };
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    navigate('/login');
  };

  return (
    <nav className="bg-white/60 backdrop-blur-md shadow-2xl border-b border-gray-200 sticky top-0 z-50">
      <div className="max-w-6xl mx-auto px-4">
        <div className="flex justify-between items-center h-20">
          <div className="flex items-center space-x-4">
            <Link to="/" className="flex items-center py-2">
              <img
                src={process.env.PUBLIC_URL + '/kkwlogo.png'}
                alt="KKW Logo"
                className="w-12 h-12 object-contain rounded-full shadow-md border-2 border-blue-200 bg-white mr-3"
              />
              <span className="font-extrabold text-gray-700 text-2xl font-serif tracking-tight drop-shadow-md">Lost & Found</span>
            </Link>
          </div>
          {/* Desktop Menu */}
          <div className="hidden md:flex items-center space-x-4">
            <>
              {isAdmin ? (
                <>
                  <Link to="/admin" className="py-2 px-4 text-blue-700 font-bold transition-colors duration-200">Admin Panel</Link>
                  <Link to="/admin/reports/approved" className="py-2 px-4 text-gray-600 hover:text-blue-700 font-semibold transition-colors duration-200">Approved Reports</Link>
                  <Link to="/admin/reports/rejected" className="py-2 px-4 text-gray-600 hover:text-blue-700 font-semibold transition-colors duration-200">Rejected Reports</Link>
                </>
              ) : (
                <>
                  <Link to="/" className="py-2 px-4 text-gray-600 hover:text-blue-700 font-semibold transition-colors duration-200">Home</Link>
                  <Link to="/report-lost" className="py-2 px-4 text-gray-600 hover:text-blue-700 font-semibold transition-colors duration-200">Report Lost</Link>
                  <Link to="/report-found" className="py-2 px-4 text-gray-600 hover:text-blue-700 font-semibold transition-colors duration-200">Report Found</Link>
                  <Link to="/my-reports" className="py-2 px-4 text-gray-600 hover:text-blue-700 font-semibold transition-colors duration-200">My Reports</Link>
                </>
              )}
              <button
                onClick={handleLogout}
                className="py-2 px-5 bg-red-500 text-white rounded-xl font-bold shadow hover:bg-red-600 transition-colors duration-200"
              >
                Logout
              </button>
            </>
          </div>
          {/* Mobile Hamburger */}
          <div className="md:hidden flex items-center">
            <button
              onClick={() => setMenuOpen(!menuOpen)}
              className="text-gray-700 focus:outline-none"
              aria-label="Toggle menu"
            >
              <svg className="w-8 h-8" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
              </svg>
            </button>
          </div>
        </div>
        {/* Mobile Menu */}
        {menuOpen && (
          <div className="md:hidden flex flex-col items-center space-y-2 pb-4 animate-fade-in">
            {isAdmin ? (
              <>
                <Link to="/admin" className="py-2 px-4 text-blue-700 font-bold w-full text-center" onClick={() => setMenuOpen(false)}>Admin Panel</Link>
                <button
                  onClick={() => { setMenuOpen(false); handleLogout(); }}
                  className="py-2 px-5 bg-red-500 text-white rounded-xl font-bold shadow hover:bg-red-600 transition-colors duration-200 w-full"
                >
                  Logout
                </button>
              </>
            ) : (
              <>
                <Link to="/" className="py-2 px-4 text-gray-600 hover:text-blue-700 font-semibold w-full text-center" onClick={() => setMenuOpen(false)}>Home</Link>
                <Link to="/report-lost" className="py-2 px-4 text-gray-600 hover:text-blue-700 font-semibold w-full text-center" onClick={() => setMenuOpen(false)}>Report Lost</Link>
                <Link to="/report-found" className="py-2 px-4 text-gray-600 hover:text-blue-700 font-semibold w-full text-center" onClick={() => setMenuOpen(false)}>Report Found</Link>
                <Link to="/my-reports" className="py-2 px-4 text-gray-600 hover:text-blue-700 font-semibold w-full text-center" onClick={() => setMenuOpen(false)}>My Reports</Link>
                <button
                  onClick={() => { setMenuOpen(false); handleLogout(); }}
                  className="py-2 px-5 bg-red-500 text-white rounded-xl font-bold shadow hover:bg-red-600 transition-colors duration-200 w-full"
                >
                  Logout
                </button>
              </>
            )}
          </div>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
