import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const AdminPanel = () => {
  const [reports, setReports] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchReports = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get('http://localhost:5000/api/admin/reports', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setReports(response.data);
      } catch (err) {
        setError('Failed to fetch reports');
      } finally {
        setLoading(false);
      }
    };
    fetchReports();
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    navigate('/login');
  };

  const handleMatch = async (lostId, foundId) => {
    setSuccess('');
    setError('');
    try {
      const token = localStorage.getItem('token');
      await axios.post(`http://localhost:5000/api/admin/reports/${lostId}/match/${foundId}`, {}, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setSuccess('Reports matched successfully!');
    } catch (err) {
      setError('Failed to match reports');
    }
  };

  const lostReports = reports.filter(r => r.type === 'lost');
  const foundReports = reports.filter(r => r.type === 'found');

  return (
    <div className="min-h-screen w-full flex flex-col items-center justify-center relative bg-gradient-to-br from-gray-900 to-blue-900 py-12">
      <div className="max-w-5xl w-full mx-auto rounded-3xl shadow-2xl bg-white/30 backdrop-blur-2xl p-10 border border-white/40 card-3d">
        <div className="flex flex-col md:flex-row justify-between items-center mb-8 gap-4">
          <h1 className="text-4xl font-extrabold text-white font-serif tracking-tight drop-shadow-lg">Admin Panel</h1>
          <button onClick={handleLogout} className="py-2 px-5 bg-red-500 text-white rounded-xl font-bold shadow hover:bg-red-600 transition-colors duration-200">Logout</button>
        </div>
        <div className="mb-6 text-white/90 text-center font-medium">Welcome, Admin! Here you can view, match, and approve lost and found reports.</div>
        {success && <div className="bg-green-100 border border-green-400 text-green-700 px-4 py-3 rounded mb-4">{success}</div>}
        {error && <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">{error}</div>}
        {loading ? (
          <div className="flex justify-center items-center min-h-[200px]">
            <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
          </div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
            <div>
              <h2 className="text-xl font-bold text-red-700 mb-3">Lost Reports</h2>
              {lostReports.length === 0 ? (
                <p className="text-gray-200">No lost reports</p>
              ) : (
                <div className="space-y-4">
                  {lostReports.map((report) => (
                    <div key={report._id} className="bg-white/80 p-4 rounded-xl shadow card-3d border border-white/40">
                      <h3 className="text-lg font-semibold mb-1 text-gray-800">{report.item.name}</h3>
                      <p className="text-gray-600 mb-1">{report.item.description}</p>
                      <p className="text-gray-600 mb-1">Location: {report.location}</p>
                      <p className="text-sm text-gray-500 mb-1">Reported by: {report.user?.name} ({report.user?.email})</p>
                    </div>
                  ))}
                </div>
              )}
            </div>
            <div>
              <h2 className="text-xl font-bold text-green-700 mb-3">Found Reports</h2>
              {foundReports.length === 0 ? (
                <p className="text-gray-200">No found reports</p>
              ) : (
                <div className="space-y-4">
                  {foundReports.map((report) => (
                    <div key={report._id} className="bg-white/80 p-4 rounded-xl shadow card-3d border border-white/40">
                      <h3 className="text-lg font-semibold mb-1 text-gray-800">{report.item.name}</h3>
                      <p className="text-gray-600 mb-1">{report.item.description}</p>
                      <p className="text-gray-600 mb-1">Location: {report.location}</p>
                      <p className="text-sm text-gray-500 mb-1">Reported by: {report.user?.name} ({report.user?.email})</p>
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>
        )}
        {/* Matching UI (for demo, just show IDs to match) */}
        <div className="mt-10">
          <h2 className="text-2xl font-bold text-white mb-4">Match Lost & Found Reports</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
            <div>
              <h3 className="text-lg font-bold text-red-400 mb-2">Lost Reports</h3>
              <ul className="text-white/90">
                {lostReports.map(lost => (
                  <li key={lost._id} className="mb-2">
                    <span className="font-semibold">{lost.item.name}</span> (ID: {lost._id})
                  </li>
                ))}
              </ul>
            </div>
            <div>
              <h3 className="text-lg font-bold text-green-400 mb-2">Found Reports</h3>
              <ul className="text-white/90">
                {foundReports.map(found => (
                  <li key={found._id} className="mb-2">
                    <span className="font-semibold">{found.item.name}</span> (ID: {found._id})
                  </li>
                ))}
              </ul>
            </div>
          </div>
          <div className="mt-6 flex flex-col md:flex-row gap-4 items-center">
            <input type="text" placeholder="Lost Report ID" id="lostId" className="rounded-lg px-4 py-2 border border-gray-300" />
            <input type="text" placeholder="Found Report ID" id="foundId" className="rounded-lg px-4 py-2 border border-gray-300" />
            <button
              onClick={() => {
                const lostId = document.getElementById('lostId').value;
                const foundId = document.getElementById('foundId').value;
                if (lostId && foundId) handleMatch(lostId, foundId);
              }}
              className="py-2 px-6 bg-blue-600 text-white rounded-xl font-bold shadow hover:bg-blue-700 transition-colors duration-200"
            >
              Match Reports
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AdminPanel;
